src/layout/country-layout.css.content:
/* Improved layout for better responsive behavior and no-scroll on home */
.country-layout {
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: #ffffff;
  overflow: hidden;
}

.country-body {
  display: flex;
  flex: 1;
  height: calc(100vh - 68px);
  overflow: hidden;
}

.country-sidebar {
  flex-shrink: 0;
}

.country-content-wrapper {
  flex: 1;
  overflow-y: auto;
  background: #ffffff;
  scrollbar-width: none;
}

/* Footer styling */
.country-footer {
  padding: 24px 32px;
  text-align: center;
  font-size: 12px;
  background: #0c1e28;
  color: #a0b0b8;
  border-top: 1px solid #1d3a4a;
  font-weight: 500;
  letter-spacing: 0.5px;
  font-family: Cormorant Garamond, serif;
  font-style: italic;
}

.footer-social {
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-bottom: 16px;
}

.social-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  color: #a0b0b8;
  transition: all 0.2s ease;
}

.social-icon:hover {
  background: rgba(255, 255, 255, 0.2);
  color: #fff;
  transform: translateY(-2px);
}

.footer-line {
  height: 1px;
  background: #e0e0e0;
  margin-bottom: 12px;
  display: none;
}

.country-loading {
  padding: 60px 40px;
  text-align: center;
  font-size: 16px;
  color: #999;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 400px;
  font-style: italic;
}

/* Responsive: Tablet view */
@media (max-width: 1024px) {
  .country-body {
    height: calc(100vh - 68px);
  }
}

/* Responsive: Mobile - stack vertically */
@media (max-width: 860px) {
  .country-body {
    flex-direction: column;
  }

  .country-sidebar {
    display: none;
  }

  .country-content-wrapper {
    flex: 1;
    order: -1;
  }
}

/* Responsive: Small mobile */
@media (max-width: 480px) {
  .country-layout {
    height: 100vh;
     display: flex;
  flex-direction: column-reverse;
  }

  .country-loading {
    padding: 40px 20px;
    font-size: 14px;
  }
}

/* Refined mobile header for Wikipedia aesthetic */
.mobile-wiki-header {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem 1rem;
  background: #fff;
  border-bottom: 1px solid #ccc;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.03);
  position: sticky;
  top: 0;
  z-index: 100;
}

/* Added mobile header top container for logo and menu button side by side */
.mobile-header-top {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  margin-bottom: 0.75rem;
}

.mobile-logo-large {
  font-size: 1.8rem;
  cursor: pointer;
  flex: 1;
  text-align: left;
}

/* Added mobile search bar styles for country pages */
.mobile-search-bar {
  width: 100%;
  padding: 0.5rem 0;
  margin-bottom: 0.75rem;
}

.mobile-search-input {
  width: 100%;
  padding: 10px 16px;
  border: 1px solid #ccc;
  border-radius: 20px;
  font-size: 14px;
  outline: none;
  background: #f8f8f8;
  transition: border-color 0.2s, box-shadow 0.2s;
  font-family: inherit;
}

.mobile-search-input:focus {
  border-color: #8b0000;
  box-shadow: 0 0 0 3px rgba(139, 0, 0, 0.1);
  background: #fff;
}

.mobile-search-input::placeholder {
  color: #999;
}
/* </CHANGE> */

.logo-wiki {
  color: #333;
}

.logo-conflicts {
  color: #d32f2f;
}

.mobile-country-title {
  font-size: 1.6rem;
  margin: 0;
  text-align: center;
  color: var(--wiki-black);
  font-family: var(--wiki-font-serif);
  border-bottom: 1px solid #a2a9b1;
  padding-bottom: 0.5rem;
  width: 100%;
}

.mobile-menu-toggle {
  background: none;
  border: none;
  cursor: pointer;
  padding: 10px;
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.hamburger-line {
  width: 30px;
  height: 3px;
  background-color: #333;
  border-radius: 2px;
}

.mobile-content-padding {
  padding: 1rem;
}

/* Styling the mobile header specific elements */
.mobile-menu-toggle-btn {
  background: transparent;
  border: 1px solid #a2a9b1;
  border-radius: 2px;
  padding: 8px 16px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: background 0.2s;
  flex-shrink: 0;
}

.mobile-menu-toggle-btn:active {
  background: #f0f0f0;
}

.hamburger-box {
  width: 16px;
  height: 12px;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.hamburger-box::before,
.hamburger-box::after,
.hamburger-inner {
  content: "";
  display: block;
  width: 100%;
  height: 2px;
  background: #000;
}

.menu-label {
  font-size: 0.9rem;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #444;
}

/* Wiki styling tweaks */
.logo-wiki {

    font-family: Cormorant Garamond, serif;
    font-style: italic;
    font-weight: 100;
  color: #ddf5ffd0;
    background:  #061b23;
    padding: 10px;
    border-radius: 50%;

}
.logo-conflicts {
  font-family: Cormorant Garamond,serif;

  font-weight: 900;
    color: #0c3344;
}

.menu-icon {
  font-size: 1.2rem;
  line-height: 1;
}

src/index.css.content:
/* Enhanced global styles with professional color scheme */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
    "Droid Sans", "Helvetica Neue", sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  background: #ffffff;
  color: #1a1a1a;
}

code {
  font-family: source-code-pro, Menlo, Monaco, Consolas, "Courier New", monospace;
}

/* Focus visible styles for accessibility */
*:focus-visible {
  outline: 2px solid #8b0000;
  outline-offset: 2px;
}

button:focus-visible,
a:focus-visible,
input:focus-visible {
  outline: 2px solid #8b0000;
  outline-offset: 2px;
}

/* Smooth transitions for all interactive elements */
button,
a,
input,
select,
textarea {
  transition: all 0.2s ease;
}

/* Custom scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: #f0f0f0;
}

::-webkit-scrollbar-thumb {
  background: #8b0000;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #6b0000;
}

src/layout/country-header.css.content:
/* ======================================
   HEADER STYLING - PROFESSIONAL NEWSPAPER
   ====================================== */

/* === MAIN HEADER CONTAINER === */
.country-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px 24px;
  gap: 20px;
  position: sticky;
  top: 0;
  z-index: 100;
}

/* === LEFT SECTION: LOGO + QUOTE === */
.header-left {
  display: flex;
  flex-direction: column;
  cursor: pointer;
  user-select: none;
  outline: none;
  flex-shrink: 0;
}

.header-left:focus {
  box-shadow: 0 0 0 3px rgba(139, 0, 0, 0.08);
  border-radius: 6px;
}

.header-logo {
  display: flex;
  align-items: baseline;
  gap: 1px;
}

.logo-wiki {
  font-family: Georgia, "Times New Roman", serif;
  font-weight: 300;
  font-size: 22px;
  color: #111;
  letter-spacing: 1px;
}

.logo-conflicts {
  font-family: Inter, ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial;
  font-weight: 700;
  font-size: 22px;
  color: #111;
  text-transform: lowercase;
}

.header-quote {
  font-size: 20px;
  color: #1a6483;
  font-weight: 500;
  letter-spacing: 0.3px;
  font-family: Bonheur Royale;
}

/* === CENTER SECTION: SEARCH === */
.header-center {
  flex: 1;
  display: flex;
  justify-content: center;
}

.search-wrapper {
  width: 100%;
  max-width: 520px;
  display: flex;
  justify-content: center;
}

.header-search {
  width: 100%;
  max-width: 480px;
  padding: 10px 18px;
  border-radius: 24px;
  border: 1px solid #ddd;
  outline: none;
  font-size: 14px;
  background: #f8f8f8;
  transition: box-shadow 0.18s, border-color 0.18s, background 0.18s;
  font-family: Cormorant Garamond, serif;
}

.header-search:focus {
  border-color: #8b0000;
  background: #fff;
  box-shadow: 0 4px 12px rgba(139, 0, 0, 0.08);
}

.header-search::placeholder {
  color: #999;
}

/* === RIGHT SECTION: LANGUAGE SELECTOR === */
.header-right {
  position: relative;
  display: flex;
  align-items: center;
  gap: 10px;
  flex-shrink: 0;
}

/* -------- RED LANGUAGE SELECTOR -------- */
.lang-toggle {
  width: 42px;
  height: 42px;
  border-radius: 50%;
  border: none;
  background: #8b0000;
  color: #fff;
  font-size: 18px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
  box-shadow: 0 2px 6px rgba(139, 0, 0, 0.15);
}

.lang-toggle:hover {
  transform: scale(1.05);
  box-shadow: 0 4px 10px rgba(139, 0, 0, 0.2);
}

.lang-toggle:active {
  transform: scale(0.98);
}

.lang-toggle:focus {
  outline: 2px solid #8b0000;
  outline-offset: 2px;
}

.lang-menu {
  position: absolute;
  top: calc(100% + 12px);
  right: 0;
  background: white;
  padding: 12px;
  border-radius: 12px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
  display: flex;
  flex-direction: column;
  gap: 8px;
  opacity: 0;
  transform: scale(0.92) translateY(-8px);
  pointer-events: none;
  transition: all 0.22s cubic-bezier(0.2, 0.9, 0.3, 1);
  will-change: transform, opacity;
  min-width: 180px;
}

.lang-menu.open {
  opacity: 1;
  transform: scale(1) translateY(0);
  pointer-events: auto;
}

.lang-option {
  display: flex;
  align-items: center;
  gap: 12px;
  width: 100%;
  padding: 10px 14px;
  border-radius: 8px;
  border: 1px solid #e0e0e0;
  background: #fff;
  cursor: pointer;
  font-size: 14px;
  transition: all 0.14s ease;
  color: #333;
  font-weight: 500;
}

.lang-option:hover {
  background: #f8f8f8;
  border-color: #8b0000;
  color: #8b0000;
  transform: translateX(4px);
}

.lang-option.active {
  background: #8b0000;
  border-color: #8b0000;
  color: #fff;
  font-weight: 600;
  box-shadow: 0 4px 12px rgba(139, 0, 0, 0.2);
}

.flag-emoji {
  font-size: 20px;
  line-height: 1;
}

.lang-label {
  flex: 1;
  text-align: left;
}

/* -------- COLLABORATE BUTTON -------- */
.collaborate-button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: none;
  border-radius: 9999px;
  padding: 0.6rem 1.3rem;
  background: linear-gradient(to right, #8b0000, #a02020);
  color: white;
  font-family: "Cinzel", serif;
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  box-shadow: 0 4px 6px rgba(139, 0, 0, 0.2);
  text-decoration: none;
}

.collaborate-button:hover {
  transform: scale(1.05);
  box-shadow: 0 6px 12px rgba(139, 0, 0, 0.3);
}

/* -------- GREEN LANGUAGE SELECTOR -------- */
.language-button-green {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: none;
  border-radius: 9999px;
  padding: 0.6rem 1.3rem;
  background: linear-gradient(to right, #061b23, #0d394a);
  color: white;
  font-family: "Cinzel", serif;
  font-weight: 600;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}

.language-button-green:hover {
  transform: scale(1.05);
}

.language-dropdown {
  display: none;
  position: absolute;
  top: calc(100% + 12px);
  right: 0;
  background: white;
  padding: 12px;
  border-radius: 12px;
  flex-direction: row;
  gap: 12px;
  /* box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15); */
  z-index: 2000;
}

.language-dropdown.open {
  display: flex;
      flex-direction: column;
      scale: 0.9;
      margin-top: -20px;
}

.language-flag-circle {
  width: 55px;
  height: 55px;
  border-radius: 50%;
  overflow: hidden;
  cursor: pointer;
  box-shadow: 0 4px 10px rgba(0,0,0,0.2);
  transition: transform 0.2s ease;
}

.language-flag-circle:hover {
  transform: scale(1.2);
}

.language-flag-circle.active {
  border: 5px solid #125975;
}

.language-flag-circle img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* ======================================
   RESPONSIVE STYLING
   ====================================== */
@media (max-width: 860px) {
  .country-header {
    padding: 12px 16px;
    gap: 12px;
  }

  .header-center {
    display: none;
  }

  .logo-wiki,
  .logo-conflicts {
    font-size: 18px;
  }

  .header-quote {
    display: none;
  }

  .lang-toggle {
    width: 38px;
    height: 38px;
    font-size: 16px;
  }

  .lang-menu {
    min-width: 160px;
    right: -8px;
  }
}

@media (max-width: 600px) {
  .country-header {
    padding: 10px 12px;
    gap: 8px;
  }

  .lang-toggle {
    width: 36px;
    height: 36px;
