.timeline-container {
  padding: 2rem;
  max-width: 1000px;
  margin: 0 auto;
}

.timeline-header {
  text-align: center;
  margin-bottom: 3rem;
}

.timeline-header h2 {
  font-family: 'Cormorant Garamond', serif;
  font-size: 2.5rem;
  color: #0c3344;
  margin-bottom: 0.5rem;
  font-weight: 600;
}

.timeline-header p {
  font-family: 'Cormorant Garamond', serif;
  font-style: italic;
  color: #666;
  font-size: 1.1rem;
}

.timeline-line {
  position: relative;
  padding-left: 2rem;
}

.timeline-line::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  background: linear-gradient(to bottom, #0c3344, #1d546b);
}

.timeline-event {
  position: relative;
  margin-bottom: 3rem;
  padding-left: 2rem;
  cursor: pointer;
  transition: transform 0.2s ease;
}

.timeline-event:hover {
  transform: translateX(5px);
}

.timeline-event::before {
  content: '';
  position: absolute;
  left: -2rem;
  top: 0.5rem;
  width: 14px;
  height: 14px;
  background: #0c3344;
  border-radius: 50%;
  border: 3px solid #fff;
  box-shadow: 0 0 0 3px #0c3344;
}

.timeline-date {
