  font-size: 1rem;
  color: #444;
  line-height: 1.6;
}

.timeline-article {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
  padding: 2rem;
  background: #fff;
}

.timeline-article-media {
  position: relative;
}

.timeline-article-media img,
.timeline-article-media video {
  width: 100%;
  height: auto;
  object-fit: cover;
  border-radius: 4px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
}

.timeline-article-content {
