/* Update mobile menu overlay styles */
.mobile-menu-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: white;
  z-index: 2000;
  display: flex;
  flex-direction: column;
  animation: slideIn 0.3s ease-out;
}

@keyframes slideIn {
  from {
    transform: translateX(100%);
  }
  to {
    transform: translateX(0);
  }
}

/* Update header styles */
.mobile-menu-header {
  padding: 16px 20px;
  border-bottom: 1px solid #eee;
  display: flex;
  align-items: center;
  gap: 16px;
  background: #f8f9fa;
  justify-content: flex-start; /* Ensure alignment */
}

.close-button {
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  padding: 4px 8px;
}

.menu-title {
  margin: 0;
  font-size: 1.2rem;
  font-weight: bold;
  border-bottom: none; /* Remove global h2 border if inherited */
